hudson.tasks.Shell.command:
git config --global user.email "you@example.com";
git config --global user.name "Your Name";

eval "echo $HOME";

echo "Host *
    StrictHostKeyChecking no" > ~/.ssh/config;

[ -f ~/.ssh/id_rsa ] && rm ~/.ssh/id_rsa;
[ -f ~/.ssh/id_rsa.pub ] && rm ~/.ssh/id_rsa.pub;

ssh-keygen -q -t rsa -N "" -f ~/.ssh/id_rsa;
eval "eval `ssh-agent -s`";
eval "ssh-add ~/.ssh/id_rsa";
eval "CCTRL_EMAIL=$CCTRL_EMAIL CCTRL_PASSWORD=$CCTRL_PASSWORD cctrluser key.add ~/.ssh/id_rsa.pub";

eval "cd $WORKSPACE";
eval "git init";
eval "git add *";
eval "git remote add cctrl ssh://$CCTRL_APP@cloudcontrolled.com/repository.git";
eval "git commit -m \"commit\"";
eval "git push -f cctrl HEAD:$CCTRL_DEPLOYMENT";
eval "CCTRL_EMAIL=$CCTRL_EMAIL CCTRL_PASSWORD=$CCTRL_PASSWORD cctrlapp $CCTRL_APP/$CCTRL_DEPLOYMENT deploy";
